export const initialState = {
  message: '',
  myNumber: 1,
  choice: {}
}

export const reducer = (state = {}, action) => {
  switch(action.type) {
    case 'ADD_YOUR_CHOICE':
      console.log('Play Reducer', 'ADD_YOUR_CHOICE')
      if (action.number) {

      }
      return {
        ...state,
        choice: state.choice['player'].concat(state.myNumber)
      }
    case 'ADD_COMPUTER_CHOICE':
      console.log('Play Reducer', 'ADD_COMPUTER_CHOICE')
      return {
        ...state,
        choice: state.choice['computer'].concat(() => (Math.random() * (100 - 0) + 0))
      }
    default:
      console.log('Play Reducer', 'unrecognised type')
      return {
        ...state
      }
  }
}

// POUR : i de 1 a 100
// SI : i % nombre choisi == 0 -> diviseurs communs donc on repond
// SINON SI : POUR : j de 1 a 100 -> i * j == nombre choisi -> multiple donc on repond
// SINON : RANDOM DE 1 A 100
