      // c'est un diviseur commun
      console.log(i);
      return i
    } else {
      for (let j = MIN_NUMBER+1; j < MAX_NUMBER; j++) {
        if ((j * i) === chosenNumber) {
          // c'est un multiple
          return j
        } else {
          // TROUVE PAS
          return false
        }
      }
    }
  }
}

export const reducer = (state = {}, action) => {
  switch(action.type) {
    case 'ADD_YOUR_CHOICE':
      console.log('Play Reducer', 'ADD_YOUR_CHOICE')

