      for (let i = MIN_NUMBER+1; i < MAX_NUMBER; i++) {
        if (i % state.currentNumber && !state.computerChoices.includes(i)) {
          // c'est un diviseur commun
          return {
            ...state,
            message: state.message = "It is your turn",
            currentNumber: i,
            computerChoices: state.computerChoices.concat(i),
            turn: state.turn = "player"
          }
        } else {
          for (let j = MIN_NUMBER+1; j < MAX_NUMBER; j++) {
            if ((j * i) === state.currentNumber) {
              // c'est un multiple
