export const initialState = {
  message: '',
  playerChoices: [],
  computerChoices: [],
  isLoose: false,
  turn: 'player'
}

export const reducer = (state = {}, action) => {
  switch(action.type) {
    case 'ADD_YOUR_CHOICE':
      console.log('Play Reducer', 'ADD_YOUR_CHOICE')

      let isAlreadySend = false;
      for (let i = 0; i < state.playerChoices.length || i < state.computerChoices.length; i++) {
        console.log(state.playerChoices[i]);
        if (state.playerChoices[i] === action.number) {
          isAlreadySend = true
        } else if (state.computerChoices[i] === action.number) {
          isAlreadySend = true
        }
      }

      if (isAlreadySend) {
        return {
          ...state,
          message: state.message = "This number was already send, you loose",
          isLoose: state.isLoose = true
        }
      } else if (action.number) {
        return {
          ...state,
          playerChoices: state.playerChoices.concat(action.number),
          message: state.message = `You send ${action.number}`,
          turn: state.turn = "computer"
        }
      } else {
        return {
          ...state,
          message: state.message = "Sorry, is not a correct entry, please resend"
        }
      }

    case 'ADD_COMPUTER_CHOICE':
      console.log('Play Reducer', 'ADD_COMPUTER_CHOICE')
      return {
        ...state,
        choices: state.choices['computer'].concat(() => (Math.random() * (100 - 0) + 0))
      }
    case 'RESET':
      return {
        ...state,
        message: '',
        playerChoices: [],
        computerChoices: [],
        isLoose: false,
        turn: 'player'
      }
    default:
      console.log('Play Reducer', 'unrecognised type')
      return {
        ...state
      }
  }
}

// CHOOSE
// POUR : i de 1 a 100
// SI : i % nombre choisi == 0 -> diviseurs communs donc on repond
// SINON SI : POUR : j de 1 a 100 -> i * j == nombre choisi -> multiple donc on repond
// SINON : JE SUIS BLOQUE

// VERIFICATION
// POUR : i de 1 a 100
// SI : i % nombre donne == 0 -> OK DIVISEUR
// SINON SI : POUR : j de 1 a 100 -> i * j == nombre donne -> OK MULTIPLE
// SINON : LOOSE
